SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE generate_tapped_dose_records ( cd_material_p bigint, cd_unidade_medida_p text, qt_dose_p bigint, qt_dias_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, nr_seq_mat_cpoe_p bigint, first_record_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_material_w	cpoe_material.nr_sequencia%type;
dt_fim_w		timestamp		:= (dt_fim_p - 1/1440);
ie_duracao_w		varchar(10)	:= 'P';


BEGIN
nr_seq_material_w := null;

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') and (cd_unidade_medida_p IS NOT NULL AND cd_unidade_medida_p::text <> '') and (nr_seq_mat_cpoe_p IS NOT NULL AND nr_seq_mat_cpoe_p::text <> '') then
	
	if (first_record_p = 'S') then		
		if (dt_fim_p IS NOT NULL AND dt_fim_p::text <> '') then

			update	cpoe_material
			set	ie_duracao = 'P',
				dt_fim = (dt_fim_p - 1/1440),
				qt_dose = qt_dose_p
			where	nr_sequencia = nr_seq_mat_cpoe_p;

		end if;
		
		insert into cpoe_tappering_dose(
				nr_sequencia,
				nr_seq_mat_cpoe,
				cd_unidade_medida,
				qt_dose,
				dt_inicio,
				dt_fim,
				dt_atualizacao,
				nm_usuario)
			values (
				nextval('cpoe_tappering_dose_seq'),
				nr_seq_mat_cpoe_p,
				cd_unidade_medida_p,
				qt_dose_p,
				dt_inicio_p,
				dt_fim_w,
				clock_timestamp(),
				nm_usuario_p);
	
	else
		if (coalesce(qt_dias_p,0) = 0) then
			dt_fim_w	:= null;
			ie_duracao_w	:= 'C';
		end if;
		
		nr_seq_material_w := cpoe_duplicar_registro('CPOE_MATERIAL', nm_usuario_p, nr_seq_mat_cpoe_p, nr_seq_material_w);
		
		if (coalesce(nr_seq_material_w,0) > 0) then
			update	cpoe_material
			set	ie_duracao = ie_duracao_w,
				dt_fim = dt_fim_w,
				dt_inicio = dt_inicio_p,
				qt_dose = qt_dose_p,
				nr_seq_cpoe_anterior  = NULL
			where	nr_sequencia = nr_seq_material_w;
		
		
			insert into cpoe_tappering_dose(
				nr_sequencia,
				nr_seq_mat_cpoe,
				cd_unidade_medida,
				qt_dose,
				dt_inicio,
				dt_fim,
				dt_atualizacao,
				nm_usuario)
			values (
				nextval('cpoe_tappering_dose_seq'),
				nr_seq_material_w,
				cd_unidade_medida_p,
				qt_dose_p,
				dt_inicio_p,
				dt_fim_w,
				clock_timestamp(),
				nm_usuario_p);
						
		end if;
		
	end if;
	
commit;
	
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
