CREATE OR REPLACE PROCEDURE generate_task_needs_schedule ( nr_atendimento_p atendimento_paciente.nr_atendimento%type, cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, nr_seq_item_cpoe_p cpoe_material.nr_sequencia%type, ie_tipo_item_cpoe_p wl_worklist.ie_tipo_item_cpoe%type, dt_liberacao_p cpoe_material.dt_liberacao%type, dt_liberacao_farm_p cpoe_material.dt_liberacao_farm%type, dt_liberacao_farm_old_p cpoe_material.dt_liberacao_farm%type, dt_inicio_p cpoe_material.dt_inicio%type, ie_alterou_horario_p cpoe_material.ie_alterou_horario%type, ie_sem_aprazamento_p prescr_material.ie_sem_aprazamento%type) AS $body$
DECLARE


nr_seq_wl_item_w	wl_perfil.nr_sequencia%type;


BEGIN
if (ie_alterou_horario_p = 'S'
	and ie_sem_aprazamento_p = 'S'
	and (dt_liberacao_p IS NOT NULL AND dt_liberacao_p::text <> '') 
	and coalesce(dt_liberacao_farm_old_p::text, '') = '' 
	and (dt_liberacao_farm_p IS NOT NULL AND dt_liberacao_farm_p::text <> '')) then

	nr_seq_wl_item_w := obter_se_worklist_lib_cat('NS');

	if (nr_seq_wl_item_w IS NOT NULL AND nr_seq_wl_item_w::text <> '') then
		CALL gerar_registro_worklist(nr_atendimento_p,
					clock_timestamp(), 
					wheb_usuario_pck.get_nm_usuario, 
					coalesce(dt_inicio_p, clock_timestamp()),
					coalesce(dt_inicio_p, clock_timestamp()) + 1, 
					cd_pessoa_fisica_p, 
					nr_seq_wl_item_w, 
					nr_seq_item_cpoe_p, 
					ie_tipo_item_cpoe_p,
				       	'N');
	end if;
end if;

CALL generate_task_needs_ack(
		nr_atendimento_p,
		cd_pessoa_fisica_p, 
		nr_seq_item_cpoe_p, 
		'M', 
		dt_liberacao_p, 
		dt_liberacao_farm_p, 
		dt_liberacao_farm_old_p, 
		dt_inicio_p);

end;
$body$
