CREATE OR REPLACE PROCEDURE generat_evaluation_release_rep ( nr_prescricao_p prescr_medica.nr_prescricao%type, nm_usuario_p usuario.nm_usuario%type, ds_mensagem_p INOUT text ) AS $body$
DECLARE


	c25 CURSOR FOR
		SELECT	a.nr_sequencia nr_seq_avaliacao,
				a.nr_seq_tipo_avaliacao
		from	med_avaliacao_paciente a
		where	a.nr_prescricao = nr_prescricao_p;

BEGIN

	for c25_w in c25
	loop
		CALL Gerar_Evolucao_Avaliacao_Pac(c25_w.nr_seq_avaliacao, nm_usuario_p);
		ds_mensagem_p := gerar_mensagem_avaliacao(c25_w.nr_seq_avaliacao, c25_w.nr_seq_tipo_avaliacao, ds_mensagem_p);
	end loop;

exception
	when no_data_found then
		null;
    when others then
		null;
end;
$body$
