CREATE OR REPLACE PROCEDURE gen_dva_account_batch_request (nr_batch_conta_p bigint, --account batch number patient billing
 cd_estabelecimento_p bigint, nm_usuario_p text, cd_error_string_p INOUT text) AS $body$
DECLARE

pending_w varchar(30) :='pending';
cd_transaction_w  varchar(25);
c03 CURSOR FOR
	SELECT  a.nr_interno_conta
	from	conta_paciente a
	where	a.nr_seq_protocolo 	= nr_batch_conta_p
	and	get_eclipse_claim_status(a.nr_interno_conta, 'claim') = pending_w; -- also put the claim status check as well, only send those claims for which the recipt is not generated  
	
BEGIN

for r03 in c03 loop
  cd_transaction_w := dva_claim_request(nm_usuario_p, cd_estabelecimento_p, null, r03.nr_interno_conta, r03.nr_interno_conta, 'N', 'S', cd_transaction_w);
end loop;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
