CREATE OR REPLACE PROCEDURE geracao_diaria_itens_atend ( dt_parametro_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
nr_atendimento_w	bigint;
qt_regra_evento_w	bigint;
					
C00 CURSOR FOR 
   SELECT 	e.nr_atendimento 
	from 	atendimento_paciente e 
    where 	e.dt_entrada  <= dt_parametro_p 
     and 	coalesce(e.dt_fim_conta::text, '') = '' 
	 and 	coalesce(e.dt_cancelamento::text, '') = '' 
	 and 	qt_regra_evento_w > 0 
     and 	coalesce(e.dt_alta,to_date('2999','yyyy')) > dt_parametro_p;
			

BEGIN 
 
-- Evento Geração diária de itens por atendimento 
select	count(*) 
into STRICT	qt_regra_evento_w 
from 	regra_lanc_automatico 
where 	nr_seq_evento = 544 
and 	ie_situacao = 'A';
 
OPEN C00;
LOOP 
FETCH C00 into 
	nr_atendimento_w;
	EXIT WHEN NOT FOUND; /* apply on c00 */	
	BEGIN	 
	 
	CALL Gerar_lancamento_automatico(nr_atendimento_w, null, 544, nm_usuario_p, null, null,null,null,null,null);
	 
	END;
END LOOP;
CLOSE C00;
	 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
