SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE geracao_diaria_itens_conta (nr_atendimento_p bigint) AS $body$
DECLARE

 qt_dias_pagamento_w    pessoa_fisica_taxa.qt_dias_pagamento%type;
 qt_regra_evento_w     smallint;

BEGIN
 
select	count(*) 
into STRICT	qt_regra_evento_w 
from	regra_lanc_automatico 
where	nr_seq_evento = 593 
and	ie_situacao = 'A';
 
select	coalesce(max(pft.qt_dias_pagamento),0) 
into STRICT	qt_dias_pagamento_w 
from 	atendimento_paciente ap, 
	pessoa_fisica_taxa pft 
where	qt_regra_evento_w > 0 
and	pft.cd_pessoa_fisica = ap.cd_pessoa_fisica 
and	(pft.qt_dias_pagamento IS NOT NULL AND pft.qt_dias_pagamento::text <> '') 
and	ap.nr_atendimento = nr_atendimento_p;
  
if (qt_dias_pagamento_w IS NOT NULL AND qt_dias_pagamento_w::text <> '') then 
	CALL gerar_lanc_auto_taxa_diaria(nr_atendimento_p, qt_dias_pagamento_w);
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE geracao_diaria_itens_conta (nr_atendimento_p bigint) FROM PUBLIC;

