CREATE OR REPLACE PROCEDURE gerar_abas_analise_migr ( cd_funcao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nm_objeto_w		varchar(50);
nr_seq_objeto_w		bigint;
nm_objeto_ww		varchar(50);
nr_seq_objeto_ww	bigint;
ds_objeto_w		varchar(255);
nm_objeto_java_w	varchar(50);
nr_seq_apresent_w	bigint;

c01 CURSOR FOR
SELECT	a.nm_objeto,
	a.ds_objeto,
	a.nr_seq_apresent
from	w_analise_migr_proj a
where	a.cd_funcao = cd_funcao_p
and	a.ie_objeto = 'TTabSheet'
and	not exists (
		SELECT	1
		from	dic_objeto x
		where	x.cd_funcao = a.cd_funcao
		and	x.nm_objeto_orig = a.nm_objeto
		and	x.ie_tipo_objeto = 'P')
group by
	a.nm_objeto,
	a.ds_objeto,
	a.nr_seq_apresent;


BEGIN
if (cd_funcao_p IS NOT NULL AND cd_funcao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	open c01;
	loop
	fetch c01 into	nm_objeto_w,
			ds_objeto_w,
			nr_seq_apresent_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		nm_objeto_java_w := ajustar_abas_import_migr(nm_objeto_w);

		select	nextval('dic_objeto_seq')
		into STRICT	nr_seq_objeto_w
		;

		insert into dic_objeto(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ie_tipo_objeto,
			nm_objeto,
			ds_texto,
			cd_funcao,
			nm_objeto_orig,
			nr_seq_apres)
		values (
			nr_seq_objeto_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			'P',
			nm_objeto_java_w,
			ds_objeto_w,
			cd_funcao_p,
			nm_objeto_w,
			nr_seq_apresent_w);
		end;
	end loop;
	close c01;
	CALL gerar_hier_abas_analise_migr(cd_funcao_p);
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
