CREATE OR REPLACE PROCEDURE gerar_acomod_regra_tipo_atend ( nr_sequencia_p bigint, nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
nr_seq_local_regra_w		bigint;
ie_tipo_atendimento_w		smallint;		
 
 
 

BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then 
 
	ie_tipo_atendimento_w 	:= 	obter_tipo_atendimento(nr_atendimento_p);
     
	select 		coalesce(max(nr_seq_local),0) 
	into STRICT 		nr_seq_local_regra_w 
	from 		regra_atend_acomod 
	where		ie_tipo_atendimento = ie_tipo_atendimento_w;
 
	if (nr_seq_local_regra_w > 0) then 
	 
		 
		update 		paciente_atendimento 
		set 		nr_seq_local 		= nr_seq_local_regra_w,	 
				dt_acomodacao_paciente 	= clock_timestamp(), 
				nm_usuario 		= nm_usuario_p, 
				dt_atualizacao		= clock_timestamp() 
		where 		nr_seq_atendimento 	= nr_sequencia_p;
		 
	end if;
	 
end if;
 
commit;
 
end;
