SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_acordo_os_proj ( nr_seq_proj_p bigint, dt_acordo_p timestamp, nm_usuario_p text, nr_seq_acordo_p INOUT bigint) AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(50);
nr_seq_w		bigint;
nr_seq_cliente_w	bigint;


BEGIN

nr_seq_acordo_p := 0;

select	max(a.cd_pessoa_fisica)
into STRICT	cd_pessoa_fisica_w
from	usuario a
where	a.nm_usuario = nm_usuario_p;

select	max(b.nr_seq_cliente)
into STRICT	nr_seq_cliente_w
from	proj_projeto b
where	b.nr_sequencia = nr_seq_proj_p;

select	nextval('desenv_acordo_seq')
into STRICT	nr_seq_w
;

insert	into desenv_acordo(
	NR_SEQUENCIA,
	NR_SEQ_CLIENTE,
	DT_ATUALIZACAO,
	NM_USUARIO,
	DS_TITULO,
	DT_ACORDO,
	NR_SEQ_PROJETO,
	CD_RESPONSAVEL)
values (nr_seq_w,
	nr_seq_cliente_w,
	clock_timestamp(),
	nm_usuario_p,
	'Acordo solicitado para o projeto ' || nr_seq_proj_p,
	dt_acordo_p,
	nr_seq_proj_p,
	cd_pessoa_fisica_w);

nr_seq_acordo_p := nr_seq_w;
	
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
