CREATE OR REPLACE PROCEDURE gerar_adep_controle ( nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cont_w		integer;


BEGIN

if (coalesce(nr_atendimento_p,0) > 0) then
