SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_adiantamento_classif (nr_adiantamento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_cgc_w		varchar(14);
cd_estabelecimento_w	varchar(14);
nr_seq_produto_w		bigint;
nr_seq_grupo_prod_w	bigint;
count_w			bigint;



BEGIN

if (coalesce(nr_adiantamento_p,0) > 0) then

	select	cd_cgc,
		cd_estabelecimento
	into STRICT	cd_cgc_w,
		cd_estabelecimento_w
	from	adiantamento
	where	nr_adiantamento	= nr_adiantamento_p;

	select	count(*)
	into STRICT	count_w
	from	adiantamento_classif
	where	nr_adiantamento	= nr_adiantamento_p;

	if (count_w = 0) then

		SELECT * FROM obter_produto_financeiro(cd_estabelecimento_w, null, cd_cgc_w, nr_seq_produto_w, null, null, nr_seq_grupo_prod_w) INTO STRICT nr_seq_produto_w, nr_seq_grupo_prod_w;

		if (nr_seq_grupo_prod_w IS NOT NULL AND nr_seq_grupo_prod_w::text <> '') then
			update	adiantamento
			set	nr_seq_grupo_prod	= nr_seq_grupo_prod_w
			where	nr_adiantamento		= nr_adiantamento_p;
		end if;

		if (nr_seq_produto_w IS NOT NULL AND nr_seq_produto_w::text <> '') then
			insert into adiantamento_classif(nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_adiantamento,
				nr_seq_produto)
			values (nextval('adiantamento_classif_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_adiantamento_p,
				nr_seq_produto_w);
		end if;

		commit;
	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
