CREATE OR REPLACE PROCEDURE gerar_adiantamento_dev ( nr_adiantamento_p bigint, vl_devolucao_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_moeda_w		adiantamento.cd_moeda%type;
nr_sequencia_w		adiantamento_dev.nr_sequencia%type;
/* Projeto Multimoeda - Variáveis */

vl_devolucao_estrang_w	adiantamento_dev.vl_devolucao_estrang%type;
vl_complemento_w	adiantamento_dev.vl_complemento%type;
vl_cotacao_w		cotacao_moeda.vl_cotacao%type;
vl_adto_estrang_w	adiantamento.vl_adto_estrang%type;


BEGIN

select	max(a.cd_moeda),
	max(a.vl_adto_estrang),
	max(vl_cotacao)
into STRICT	cd_moeda_w,
	vl_adto_estrang_w,
	vl_cotacao_w
from	adiantamento a
where	a.nr_adiantamento	= nr_adiantamento_p;

select	coalesce(max(a.nr_sequencia),0) + 1
into STRICT	nr_sequencia_w
from	adiantamento_dev a
where	a.nr_adiantamento	= nr_adiantamento_p;

/* Projeto Multimoeda - Verifica se o adiantamento é em moeda estrangeira, caso positivo calcula os valores antes de gravar a devolução */

if (coalesce(vl_adto_estrang_w,0) <> 0 and coalesce(vl_cotacao_w,0) <> 0) then
	vl_devolucao_estrang_w 	:= vl_devolucao_p / vl_cotacao_w;
	vl_complemento_w	:= vl_devolucao_p - vl_devolucao_estrang_w;
else
	vl_devolucao_estrang_w	:= null;
	vl_complemento_w	:= null;
	vl_cotacao_w		:= null;
end if;

insert into adiantamento_dev(	nr_sequencia,
				nr_adiantamento,
				cd_moeda,
				dt_atualizacao,
				dt_devolucao,
				vl_devolucao,
				nm_usuario,
				vl_devolucao_estrang,
				vl_complemento,
				vl_cotacao)
			values (	nr_sequencia_w,
				nr_adiantamento_p,
				cd_moeda_w,
				clock_timestamp(),
				clock_timestamp(),
				vl_devolucao_p,
				nm_usuario_p,
				vl_devolucao_estrang_w,
				vl_complemento_w,
				vl_cotacao_w);


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
