CREATE OR REPLACE PROCEDURE gerar_ad_cont_regra_nf (nr_seq_contrato_p bigint, nr_seq_item_fornec_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


cd_material_w	cot_compra_forn_item.cd_material%type;
nr_cot_compra_w		cot_compra_forn_item.nr_cot_compra%type;
nr_item_cot_compra_w	cot_compra_forn_item.nr_item_cot_compra%type;


BEGIN

select	max(cd_material),
	max(nr_cot_compra),
	max(nr_item_cot_compra)
into STRICT	cd_material_w,
	nr_cot_compra_w,
	nr_item_cot_compra_w
from	cot_compra_forn_item
where	nr_sequencia = nr_seq_item_fornec_p;

if (coalesce(nr_seq_contrato_p, 0) > 0) and (coalesce(cd_material_w, 0) > 0) then
	insert into contrato_regra_nf(
		nr_sequencia,
		nr_seq_contrato,
		dt_atualizacao,
		nm_usuario,
		cd_material,
		nr_cot_compra,
		nr_item_cot_compra,
		ie_gera_sc_automatico)
	values ( nextval('contrato_regra_nf_seq'),
		nr_seq_contrato_p,
		clock_timestamp(),
		nm_usuario_p,
		cd_material_w,
		nr_cot_compra_w,
		nr_item_cot_compra_w,
		'N');
end if;

update	cot_compra_forn_item
set	nr_seq_contrato = nr_seq_contrato_p
where	nr_sequencia = nr_seq_item_fornec_p
and	coalesce(nr_seq_contrato::text, '') = '';

CALL baixar_sc_com_contrato(nr_cot_compra_w, nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
