SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_agendas_hist_eup_js ( nr_seq_agenda_p bigint, cd_tipo_agenda_p bigint, nm_usuario_p text, cd_perfil_p bigint) AS $body$
DECLARE

 
cd_agenda_w		bigint;
cd_pessoa_fisica_w	varchar(10);
nm_paciente_w		varchar(60);
hr_inicio_w		timestamp;
ds_msg_w		varchar(255);
dt_agenda_w		timestamp;
				

BEGIN 
 
if (coalesce(nr_seq_agenda_p,0) > 0) then 
 
	ds_msg_w	:= obter_texto_tasy(94646, 1);
	 
	if (cd_tipo_agenda_p in (1,2)) then 
	 
		select max(cd_agenda), 
			max(cd_pessoa_fisica), 
			substr(max(nm_paciente),1,60), 
			max(hr_inicio) 
		into STRICT	cd_agenda_w, 
			cd_pessoa_fisica_w, 
			nm_paciente_w, 
			hr_inicio_w 
		from  agenda_paciente 
		where  nr_sequencia = nr_seq_agenda_p;
		 
		 
		 
		CALL gerar_agenda_paciente_hist(cd_agenda_w, nr_seq_agenda_p, 'CA', nm_usuario_p, ds_msg_w, cd_pessoa_fisica_w, nm_paciente_w, hr_inicio_w, cd_perfil_p);
		 
		 
	elsif (cd_tipo_agenda_p = 3) then 
 
		select max(cd_agenda), 
			max(cd_pessoa_fisica), 
			substr(max(nm_paciente),1,60), 
			max(dt_agenda) 
		into STRICT	cd_agenda_w, 
			cd_pessoa_fisica_w, 
			nm_paciente_w, 
			dt_agenda_w 
		from  agenda_consulta 
		where  nr_sequencia = nr_seq_agenda_p;
		 
		CALL gerar_agenda_consulta_hist(cd_agenda_w, nr_seq_agenda_p, 'CA', nm_usuario_p, ds_msg_w, cd_pessoa_fisica_w, nm_paciente_w, dt_agenda_w);
	 
	end if;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
