CREATE OR REPLACE PROCEDURE gerar_agenda_atendimento_email ( nr_seq_agenda_p bigint, nr_atendimento_p bigint, cd_estabelecimento_p bigint, ie_agenda_p text, nm_usuario_p text, cd_pessoa_fisica_p INOUT text) AS $body$
BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (ie_agenda_p IS NOT NULL AND ie_agenda_p::text <> '') then 
	begin 
	CALL gerar_agenda_atendimento( 
		nr_atendimento_p, 
		nr_seq_agenda_p, 
		ie_agenda_p, 
		nm_usuario_p, 
		cd_estabelecimento_p);
	 
	CALL enviar_email_regra( 
		nr_seq_agenda_p, 
		'AA', 
		nm_usuario_p, 
		cd_estabelecimento_p);
	 
	select	cd_pessoa_fisica 
	into STRICT	cd_pessoa_fisica_p 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
	end;
end if;
end;
$body$
