SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_agenda_cancel_historico ( nr_seq_agenda_p bigint, cd_motivo_p text, nm_usuario_p text, ie_opcao_p text) AS $body$
DECLARE

					
nr_sequencia_w			bigint;
ds_historico_w			varchar(4000);
ds_observacao_origem_w	varchar(4000);
ds_procedimento_w		varchar(255);
ds_motivo_w				varchar(255);
ds_agenda_w				varchar(50);
ds_agenda_ww			varchar(50);
cd_pessoa_fisica_w		varchar(255);
ds_opcao_w				varchar(255);
hr_inicio_w				timestamp;
hr_inicio_ww			timestamp;


BEGIN

select	max(ds_observacao),
	max(substr(obter_descricao_procedimento(cd_procedimento, ie_origem_proced),1,200)),
	max(hr_inicio),
	max(substr(obter_nome_agenda(cd_agenda),1,50)),
	--max(cd_pessoa_fisica) lwilbert - Estava pegando o codigo da pessoa fisica do paciente e definindo como um profissional na tabela de historico do paciente.
	obter_pf_usuario(nm_usuario_p,'C')
into STRICT	ds_observacao_origem_w,
	ds_procedimento_w,
	hr_inicio_w,
	ds_agenda_w,
	cd_pessoa_fisica_w
from	agenda_paciente
where	nr_sequencia = nr_seq_agenda_p;

if (cd_motivo_p IS NOT NULL AND cd_motivo_p::text <> '') then
	select  max(ds_motivo)
	into STRICT	ds_motivo_w
	from    agenda_motivo_cancelamento
	where   cd_motivo = cd_motivo_p;
end if;

if (ie_opcao_p = 'C') then
	ds_opcao_w	:= wheb_mensagem_pck.get_texto(300656);
elsif (ie_opcao_p = 'A') then
	ds_opcao_w	:= wheb_mensagem_pck.get_texto(300657);
end if;

ds_historico_w	:= substr(wheb_mensagem_pck.get_texto(300658, 'DS_AGENDA_W=' || ds_agenda_w || ';DS_OPCAO_W=' || ds_opcao_w ||
						';DS_PROC_W=' || ds_procedimento_w || ';DS_MOTIVO_W=' || ds_motivo_w ||
						';HR_INICIO_W=' ||PKG_DATE_FORMATERS_TZ.TO_VARCHAR(hr_inicio_w,'timestamp', ESTABLISHMENT_TIMEZONE_UTILS.GETTIMEZONE) ||
						';NM_USUARIO_P=' || nm_usuario_p), 1, 4000);
			
select	nextval('agenda_pac_hist_seq')
into STRICT	nr_sequencia_w
;

insert into agenda_pac_hist(nr_sequencia,
	nr_seq_agenda,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	dt_historico,
	nr_seq_tipo,
	ds_historico,
	cd_pessoa_fisica)
values (nr_sequencia_w,
	nr_seq_agenda_p,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	null,
	ds_historico_w,
	cd_pessoa_fisica_w);
	
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_agenda_cancel_historico ( nr_seq_agenda_p bigint, cd_motivo_p text, nm_usuario_p text, ie_opcao_p text) FROM PUBLIC;

