CREATE OR REPLACE PROCEDURE gerar_agenda_chamada_fitness ( cd_pessoa_fisica_p text, nr_seq_fitness_p bigint, nr_seq_agenda_p bigint, nm_usuario_p text) AS $body$
BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (nr_seq_fitness_p IS NOT NULL AND nr_seq_fitness_p::text <> '') then 
	 
	update	agenda_consulta 
	set	nm_usuario		= nm_usuario_p, 
		dt_atualizacao		= clock_timestamp(), 
		cd_pessoa_fisica	= cd_pessoa_fisica_p, 
		ie_status_agenda 	= 'N', 
		nm_paciente 		= substr(obter_nome_pf(cd_pessoa_fisica_p),1,60), 
		nr_seq_fitness		= nr_seq_fitness_p 
	where	nr_sequencia 		= nr_seq_agenda_p;
	 
	update	hfp_paciente 
	set	ie_status = 'A', 
		nm_usuario = nm_usuario_p, 
		dt_atualizacao = clock_timestamp() 
	where	nr_sequencia = nr_seq_fitness_p;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
