CREATE OR REPLACE PROCEDURE gerar_agenda_chamada_reab ( nr_seq_lista_p bigint, nr_seq_agenda_p bigint, nm_usuario_p text) AS $body$
DECLARE

cd_pessoa_fisica_w		varchar(10);				
cd_convenio_w			integer;
cd_proc_serv_w			bigint;
nr_seq_proc_interno_serv_w	bigint;
ie_origem_proced_serv_w		bigint;

cd_procedimento_w		bigint;
nr_seq_proc_interno_w		bigint;
ie_origem_proced_w		bigint;
ds_observacao_w			varchar(255);
dt_nascimento_w			timestamp;
cd_medico_resp_w		varchar(10);
cd_medico_exec_w		varchar(10);

C01 CURSOR FOR 
	SELECT	cd_procedimento, 
		nr_seq_proc_interno, 
		ie_origem_proced 
	from	rp_lista_espera_proc_adic 
	where	nr_seq_lista = nr_seq_lista_p;

BEGIN
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (nr_seq_lista_p IS NOT NULL AND nr_seq_lista_p::text <> '') then 
	select	max(cd_pessoa_fisica), 
		max(cd_convenio), 
		max(cd_procedimento), 
		max(nr_seq_proc_interno), 
		max(ie_origem_proced), 
		max(ds_observacao), 
		max(cd_medico_resp), 
		max(cd_medico_exec) 
	into STRICT	cd_pessoa_fisica_w, 
		cd_convenio_w, 
		cd_proc_serv_w, 
		nr_seq_proc_interno_serv_w, 
		ie_origem_proced_serv_w, 
		ds_observacao_w, 
		cd_medico_resp_w, 
		cd_medico_exec_w 
	from	rp_lista_espera_modelo 
	where	nr_sequencia = nr_seq_lista_p;
	 
	select 	max(dt_nascimento) 
	into STRICT 	dt_nascimento_w 
	from 	pessoa_fisica 
	where 	cd_pessoa_fisica = cd_pessoa_fisica_w;					
	 
	update	agenda_consulta 
	set		cd_pessoa_fisica	= cd_pessoa_fisica_w, 
			ie_status_agenda 	= 'N', 
			nm_paciente 		= substr(obter_nome_pf(cd_pessoa_fisica_w),1,60), 
			cd_convenio		= cd_convenio_w, 
			cd_procedimento		= cd_proc_serv_w, 
			nr_seq_proc_interno	= nr_seq_proc_interno_serv_w, 
			ie_origem_proced 	= ie_origem_proced_serv_w, 
			nr_seq_lista_reab	= nr_seq_lista_p, 
			ds_observacao		= ds_observacao_w, 
			dt_nascimento_pac	= dt_nascimento_w, 
			cd_medico_solic		= cd_medico_resp_w, 
			cd_medico		= cd_medico_exec_w, 
			nm_usuario_origem = nm_usuario_p, 
			dt_agendamento	=	clock_timestamp() 
	where	nr_sequencia 		= nr_seq_agenda_p;
	 
	 
	open C01;
	loop 
	fetch C01 into	 
		cd_procedimento_w, 
		nr_seq_proc_interno_w, 
		ie_origem_proced_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		insert into agenda_consulta_proc(	 
			nr_sequencia, 
			dt_atualizacao, 
			nm_usuario, 
			ie_executar_proc, 
			dt_atualizacao_nrec, 
			nm_usuario_nrec, 
			nr_seq_agenda, 
			cd_procedimento, 
			nr_seq_proc_interno, 
			ie_origem_proced) 
		values (	nextval('agenda_consulta_proc_seq'), 
			clock_timestamp(), 
			nm_usuario_p, 
			'S', 
			clock_timestamp(), 
			nm_usuario_p, 
			nr_seq_agenda_p, 
			cd_procedimento_w, 
			nr_seq_proc_interno_w, 
			ie_origem_proced_w);
		end;
	end loop;
	close C01;
	 
	update	rp_lista_espera_modelo 
	set	ie_status = 'T' 
	where	nr_sequencia = nr_seq_lista_p;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
