SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_agenda_col_ord_usuario (cd_agenda_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_agenda_user_w	varchar(1) := 'N';
nr_seq_ordem_w	bigint;


BEGIN
if (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	/* verificar se agenda cadastrada */

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_agenda_user_w
	from	med_ordem_agenda
	where	cd_agenda		= cd_agenda_p
	and	nm_usuario_nrec	= nm_usuario_p;

	/* gerar agenda */

	if (ie_agenda_user_w = 'N') then
		select	nextval('med_ordem_agenda_seq')
		into STRICT	nr_seq_ordem_w
		;

		insert into med_ordem_agenda(
							nr_sequencia,
							cd_agenda,
							dt_atualizacao,
							nm_usuario,
							dt_atualizacao_nrec,
							nm_usuario_nrec,
							nr_seq_apres
							)
		values (
							nr_seq_ordem_w,
							cd_agenda_p,
							clock_timestamp(),
							nm_usuario_p,
							clock_timestamp(),
							nm_usuario_p,
							1
							);
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
