CREATE OR REPLACE PROCEDURE gerar_agenda_mensal_triagem ( nm_usuario_p text, cd_estabelecimento_p bigint, cd_agenda_p bigint, dt_referencia_p timestamp, cd_pessoa_fisica_p text, cd_turno_p bigint, ds_status_mes_p INOUT text, ds_agendado_mes_p INOUT text) AS $body$
DECLARE


dt_inicio_mes_w	timestamp;
dt_final_mes_w	timestamp;
dt_referencia_w	timestamp;
ds_retorno_w	varchar(255);
ie_gerado_w		varchar(255) := 'N';


BEGIN
if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') and (dt_referencia_p IS NOT NULL AND dt_referencia_p::text <> '') then
	begin

		dt_referencia_w	:= dt_referencia_p;
		dt_inicio_mes_w	:= PKG_DATE_UTILS.start_of(dt_referencia_w,'month',0);
		dt_final_mes_w	:= pkg_date_utils.get_datetime(pkg_date_utils.end_of(dt_referencia_w, 'MONTH', 0), dt_referencia_w);


			ds_status_mes_p := Agenda_Dia_Status_Beyond(
			cd_estabelecimento_p, cd_agenda_p, dt_inicio_mes_w, dt_final_mes_w, cd_turno_p, nm_usuario_p, ds_status_mes_p);



	select	obter_select_concatenado_bv(
		'select	pkg_date_utils.extract_field(''DAY'',b.dt_agenda) ' ||
		'from	agenda_integrada_item a, ' ||
		'	agenda_consulta b ' ||
		'where	a.nr_seq_agenda_cons = b.nr_sequencia ' ||
		'and	b.cd_pessoa_fisica = :cd_pessoa_fisica ' ||
		'and	b.dt_agenda between pkg_date_utils.start_of(:dt_agenda,''MONTH'') and pkg_date_utils.end_of(:dt_agenda,''MONTH'') ' ||
		'union ' ||
		'select	pkg_date_utils.extract_field(''DAY'',b.dt_agenda) ' ||
		'from	agenda_integrada_item a, ' ||
		'	agenda_paciente b ' ||
		'where	a.nr_seq_agenda_exame = b.nr_sequencia ' ||
		'and	b.cd_pessoa_fisica = :cd_pessoa_fisica ' ||
		'and	b.dt_agenda between pkg_date_utils.start_of(:dt_agenda,''MONTH'') and pkg_date_utils.end_of(:dt_agenda,''MONTH'') ' ||
		'order by	1 ',
		'CD_PESSOA_FISICA=' || cd_pessoa_fisica_p ||
		';DT_AGENDA=' || dt_referencia_p,
		',')
	into STRICT	ds_agendado_mes_p
	;
	end;
end if;
commit;
end;
$body$
