CREATE OR REPLACE PROCEDURE gerar_agenda_normal_html ( nr_seq_agenda_p bigint, cd_cgc_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
ie_lib_autorizacao_w     varchar(15);
ie_tipo_preco_w         varchar(15);
cd_perfil_w	      integer;
nr_seq_autorizacao_w    bigint;

BEGIN
cd_perfil_w	:= wheb_usuario_pck.get_cd_perfil;
ie_lib_autorizacao_w := obter_param_usuario(871, 183, cd_perfil_w, nm_usuario_p, cd_estabelecimento_p, ie_lib_autorizacao_w);
ie_tipo_preco_w := obter_param_usuario(871, 43, cd_perfil_w, nm_usuario_p, cd_estabelecimento_p, ie_tipo_preco_w);
 
CALL gerar_agenda_normal(nr_seq_agenda_p, cd_cgc_p, nm_usuario_p, cd_estabelecimento_p);
CALL atualizar_evento_autor_conv(2, nr_seq_agenda_p, null, null, nm_usuario_p);
 
if (ie_lib_autorizacao_w = 'S') then 
  select  coalesce(max(nr_sequencia) ,0) 
  into STRICT    nr_seq_autorizacao_w 
  from   Autorizacao_Cirurgia 
  where  nr_seq_agenda = nr_seq_agenda_p;
  if (nr_seq_autorizacao_w > 0) then 
   CALL Atualizar_autor_cir_emissao(nr_seq_autorizacao_w, cd_estabelecimento_p, nm_usuario_p, ie_tipo_preco_w);
  end if;
end if;
 
CALL enviar_email_regra(nr_seq_agenda_p, 'AG', nm_usuario_p, cd_estabelecimento_p);
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
