CREATE OR REPLACE PROCEDURE gerar_agenda_normal_swing ( nr_seq_agenda_p bigint, cd_estabelecimento_p bigint, cd_cgc_p text, nm_usuario_p text) AS $body$
DECLARE

nr_seq_autor_w		bigint;
ds_valor_parametro_w	varchar(255);

BEGIN
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then 
	begin 
	CALL gerar_agenda_normal( 
		nr_seq_agenda_p, 
		cd_cgc_p, 
		nm_usuario_p, 
		cd_estabelecimento_p);
	CALL atualizar_evento_autor_conv( 
		2, 
		nr_seq_agenda_p, 
		null, 
		null, 
		nm_usuario_p);	
	ds_valor_parametro_w := obter_parametro_funcao(871, 183, nm_usuario_p);
	if (ds_valor_parametro_w = 'S') then 
		begin 
		select	max(nr_sequencia) 
		into STRICT	nr_seq_autor_w 
		from	autorizacao_cirurgia 
		where	nr_seq_agenda = nr_seq_agenda_p;
 
		if (nr_seq_autor_w IS NOT NULL AND nr_seq_autor_w::text <> '') then 
			begin 
			CALL atualizar_autor_cir_emissao( 
				nr_seq_autor_w, 
				cd_estabelecimento_p, 
				nm_usuario_p, 
				obter_parametro_funcao(871, 43, nm_usuario_p));
			end;
		end if;
		end;
	end if;
	CALL enviar_email_regra( 
		nr_seq_agenda_p, 
		'AG', 
		nm_usuario_p, 
		cd_estabelecimento_p);
	end;
end if;
end;
