CREATE OR REPLACE PROCEDURE gerar_aih_prenatal_anterior ( nr_atendimento_p bigint, nr_prenatal_p INOUT bigint, ie_sexo_p INOUT text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
 
cd_pessoa_fisica_w	varchar(10);
ie_local_nr_gestante_w	varchar(15);


BEGIN 
 
ie_local_nr_gestante_w	:= coalesce(obter_valor_param_usuario(1123, 204, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p),'A');
 
select	cd_pessoa_fisica 
into STRICT	cd_pessoa_fisica_w 
from	atendimento_paciente 
where	nr_atendimento	= nr_atendimento_p;
 
select	ie_sexo 
into STRICT	ie_sexo_p 
from	pessoa_fisica 
where	cd_pessoa_fisica = cd_pessoa_fisica_w;
 
if (ie_local_nr_gestante_w = 'P') then 
	begin 
	select	coalesce(max(nr_gestante_pre_natal),0) 
	into STRICT	nr_prenatal_p 
	from	parto 
	where	nr_atendimento = nr_atendimento_p;
	end;
elsif (ie_local_nr_gestante_w = 'A')	then 
		begin	 
		select	coalesce(max(nr_gestante_pre_natal),0) 
		into STRICT	nr_prenatal_p 
		from	atendimento_paciente 
		where	nr_atendimento	=	nr_atendimento_p;
 
		if (nr_prenatal_p	= 0) then 
			begin 
			select	coalesce(max(nr_gestante_pre_natal),0) 
			into STRICT	nr_prenatal_p 
			from	sus_aih 
			where	nr_atendimento	= nr_atendimento_p 
			and	dt_emissao	= (	SELECT	max(dt_emissao) 
							from	sus_aih 
							where	nr_atendimento = nr_atendimento_p 
							and	(nr_gestante_pre_natal IS NOT NULL AND nr_gestante_pre_natal::text <> ''));
			end;
		end if;
		end;
end if;
END;
