CREATE OR REPLACE PROCEDURE gerar_ajuste_disp_consolidacao (nm_usuario_p text, cd_estabelecimento_p bigint, ds_lista_sequencia_p text, nr_prescricao_p bigint) AS $body$
DECLARE

 
qt_pos_antes_virgula_w 	bigint;
qt_pos_depois_virgula_w 	bigint;
ds_lista_repartida_w 		varchar(255);
nr_sequencia_w				bigint;
nr_sequencia_w2				varchar(255);
i							bigint;
										

BEGIN 
if (ds_lista_sequencia_p IS NOT NULL AND ds_lista_sequencia_p::text <> '') then 
		ds_lista_repartida_w 		:= ds_lista_sequencia_p;
		for i in 0..length(ds_lista_repartida_w) loop 
				if (position('#' in ds_lista_repartida_w) > 0) then 
						qt_pos_antes_virgula_w 		:= (position('#' in ds_lista_repartida_w) - 1);
						qt_pos_depois_virgula_w 	:= (position('#' in ds_lista_repartida_w) + 1);
						nr_sequencia_w 				:= (substr(ds_lista_repartida_w,1,qt_pos_antes_virgula_w))::numeric;
						ds_lista_repartida_w		:= substr(ds_lista_repartida_w,qt_pos_depois_virgula_w,100);
					 
						CALL ajustar_dispensacao_total(	nr_sequencia_w, 
													nm_usuario_p, 
													nr_prescricao_p, 
													cd_estabelecimento_p, 
													'I');				
				end if;
		end loop;		
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
