CREATE OR REPLACE PROCEDURE gerar_ajust_proc_grupo_ageint (cd_especialidade_p bigint, ie_classif_agenda_p text, nr_seq_item_ageint_p bigint, nr_seq_ageint_p bigint, nr_seq_grupo_p bigint, nr_seq_proc_interno_p bigint, ie_lado_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_proc_interno_w	ageint_exame_adic_item.nr_seq_proc_interno%type;
nr_sequencia_w        	ageint_exame_adic_item.nr_sequencia%type;
ie_utiliza_tempo_proced_w	varchar(1);
ie_forma_checklist_w	varchar(1) := 'L';
ie_gerar_check_list_w	varchar(1);
nr_seq_ageint_check_list_w bigint;


BEGIN

ie_forma_checklist_w := Obter_Param_Usuario(869, 75, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_forma_checklist_w);
ie_utiliza_tempo_proced_w := Obter_Param_Usuario(869, 178, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_utiliza_tempo_proced_w);

	select 	max(nr_seq_proc_interno)
	into STRICT	nr_seq_proc_interno_w
	from	ageint_ajust_proc_grupo
	where	((cd_especialidade = cd_especialidade_p) or (coalesce(cd_especialidade::text, '') = ''))
	and		((ie_classif_agenda = ie_classif_agenda_p) or (coalesce(ie_classif_agenda::text, '') = ''))
	and		((cd_estabelecimento = cd_estabelecimento_p) or (coalesce(cd_estabelecimento::text, '') = ''));

	if (coalesce(nr_seq_proc_interno_w::text, '') = '') then
		nr_seq_proc_interno_w := nr_seq_proc_interno_p;
	end if;

	if (nr_seq_proc_interno_w IS NOT NULL AND nr_seq_proc_interno_w::text <> '') then

		insert into ageint_exame_adic_item(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_proc_interno,
				nr_seq_item,
				nr_seq_grupo_selec,
				ie_lado)
		values (
				nextval('ageint_exame_adic_item_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_proc_interno_w,
				nr_seq_item_ageint_p,
				nr_seq_grupo_p,
				ie_lado_p);

		commit;

		if (ie_forma_checklist_w = 'L') then

			select	substr(ageint_obter_se_proc_check(nr_seq_proc_interno_w,nr_seq_ageint_p, cd_estabelecimento_p),1,1)
			into STRICT	ie_gerar_check_list_w
			;

			if (ie_gerar_check_list_w = 'S') then
				select	nextval('ageint_check_list_paciente_seq')
				into STRICT	nr_seq_ageint_check_list_w
				;

				insert into ageint_check_list_paciente(nr_sequencia,
									nr_seq_ageint,
									dt_atualizacao,
									nm_usuario,
									ie_tipo_check_list)
								values (nr_seq_ageint_check_list_w,
									nr_seq_ageint_p,
									clock_timestamp(),
									nm_usuario_p,
									'I');

				commit;

				CALL Ageint_Gerar_Check_List(nr_seq_ageint_check_list_w,nr_seq_proc_interno_w,nr_seq_ageint_p,nm_usuario_p, cd_estabelecimento_p);

			end if;
		end if;

		if (ie_utiliza_tempo_proced_w = 'S') then
			CALL Ageint_atualiza_dur_ex_adic(nr_seq_item_ageint_p,'A','L');
		end if;

	end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
