CREATE OR REPLACE PROCEDURE gerar_alerta_alteracao_pront (cd_pessoa_fisica_p text, ie_tipo_prontuario_p bigint, nm_usuario_p text) AS $body$
DECLARE

		
		
ds_prontuario_w		varchar(255);
		

BEGIN

select 	substr(obter_valor_dominio(1528, ie_tipo_prontuario_p),1,90)
into STRICT	ds_prontuario_w
;


insert into alerta_paciente(nr_sequencia,
		cd_estabelecimento,
		cd_pessoa_fisica,
		dt_alerta,
		dt_atualizacao,
		nm_usuario,
		ds_alerta,
		ie_situacao,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		dt_fim_alerta,
		nr_seq_bo,
		cd_funcao,
		dt_liberacao,
		dt_inativacao,
		nm_usuario_inativacao,
		ds_justificativa,
		cd_perfil_ativo,
		nr_seq_tipo_alerta)
	values (nextval('alerta_paciente_seq'),
		1,
		cd_pessoa_fisica_p,
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		obter_desc_expressao(296549) || ': ' || ds_prontuario_w,
		'A',
		clock_timestamp(),
		nm_usuario_p,
		null,
		null,
		null,
		clock_timestamp(),
		null,
		null,
		null, 
		null, 
		null);	
		

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
