SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_alerta_atend_conta ( nr_atendimento_p bigint, nm_usuario_p text, ds_observacao_p text) AS $body$
DECLARE

 
nr_sequencia_w	bigint;
ds_alerta_w	varchar(2000);	
ds_paciente_w	varchar(100) := '';
ds_observacao_w	varchar(200);


BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_atendimento_p > 0) then 
 
	select	coalesce(max(obter_nome_pf(cd_pessoa_fisica)),wheb_mensagem_pck.get_texto(305785)) 
	into STRICT	ds_paciente_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
	 
	select	substr(CASE WHEN ds_observacao_p='' THEN ''  ELSE wheb_mensagem_pck.get_texto(305787, 'DS_OBSERVACAO_P=' || ds_observacao_p) END ,1,200) 
	into STRICT	ds_observacao_w 
	;
	ds_alerta_w :=	wheb_mensagem_pck.get_texto(305788, 	'DS_PACIENTE_W=' || ds_paciente_w || 
								';NR_ATENDIMENTO_P=' || nr_atendimento_p || 
								';DT_ATUALIZACAO=' || to_char(clock_timestamp(), 'dd/mm/yyyy hh24:mi:ss') || 
								';DS_USUARIO=' || substr(obter_nome_usuario(nm_usuario_p),1,100) || 
								';NM_USUARIO_P=' || nm_usuario_p || 
								';DS_OBSERVACAO_W=' || ds_observacao_w);
 
	select	nextval('atendimento_alerta_seq') 
	into STRICT	nr_sequencia_w 
	;
	 
	insert into atendimento_alerta( 
		nr_sequencia, 
		nr_atendimento, 
		dt_alerta, 
		dt_atualizacao, 
		nm_usuario, 
		ds_alerta, 
		ie_situacao, 
		dt_fim_alerta, 
		dt_liberacao, 
		dt_inativacao, 
		nm_usuario_inativacao, 
		ds_justificativa) 
	values (	 
		nr_sequencia_w, 
		nr_atendimento_p, 
		clock_timestamp(), 
		clock_timestamp(), 
		nm_usuario_p, 
		ds_alerta_w, 
		'A', 
		null, 
		null, 
		null, 
		null, 
		null);
		 
	commit;
 
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
