CREATE OR REPLACE PROCEDURE gerar_alerta_baixa_tit_pagar ( nr_titulo_p bigint, nr_seq_baixa_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_pessoa_fisica_w		varchar(10);
nr_seq_evento_w			bigint;
nr_seq_trans_fin_w		bigint;
cd_tipo_baixa_w			integer;

C01 CURSOR FOR
	SELECT	nr_seq_evento
	from	regra_envio_sms a,
		ev_evento	b
	where	coalesce(a.cd_tipo_baixa,cd_tipo_baixa_w)		= cd_tipo_baixa_w
	and	((coalesce(a.nr_seq_trans_financ,nr_seq_trans_fin_w)	= nr_seq_trans_fin_w) or (coalesce(nr_seq_trans_fin_w::text, '') = ''))
	and	a.cd_estabelecimento				= cd_estabelecimento_p
	and	a.ie_evento_disp 				= 'BTP'
	and	a.nr_seq_evento 				= b.nr_sequencia
	and	coalesce(a.ie_situacao,'A') 				= 'A';


BEGIN
select	max(cd_pessoa_fisica)
into STRICT	cd_pessoa_fisica_w
from	titulo_pagar
where	nr_titulo	= nr_titulo_p;

if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
	select	max(cd_tipo_baixa),
		max(nr_seq_trans_fin)
	into STRICT	cd_tipo_baixa_w,
		nr_seq_trans_fin_w
	from	titulo_pagar_baixa
	where	nr_titulo	= nr_titulo_p
	and	nr_sequencia	= nr_seq_baixa_p;

	open C01;
	loop
	fetch C01 into
		nr_seq_evento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		CALL gerar_evento_paciente(nr_seq_evento_w,null,cd_pessoa_fisica_w,null,nm_usuario_p,null,null,null,null,null,null,null,null,nr_titulo_p,nr_seq_baixa_p,'N');
		end;
	end loop;
	close C01;
end if;

/* NÃ£o pode ter commit */

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
