SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_alerta_eventos_atrasa_pj () AS $body$
DECLARE


    c01 CURSOR FOR
        SELECT pi.nr_seq_evento nr_seq_alerta,
               ev.nr_sequencia  nr_seq_evento
          FROM protocolo_int_pac_evento ev,
               protocolo_int_pac_etapa  et,
               protocolo_int_paciente   pt,
               protocolo_integrado      pi
         WHERE ev.nr_seq_prt_int_pac_etapa = et.nr_sequencia
           AND et.nr_seq_protocolo_int_pac = pt.nr_sequencia
           AND pt.nr_sequencia_protocolo = pi.nr_sequencia
           AND trunc(ev.dt_prevista) BETWEEN trunc(clock_timestamp() - interval '1 days') AND trunc(clock_timestamp()) -- eventos que estavam previstos para o dia anterior
           AND (coalesce(ev.dt_real::text, '') = '' OR ev.dt_real > ev.dt_prevista) -- que atrasaram
           AND (pi.nr_seq_evento IS NOT NULL AND pi.nr_seq_evento::text <> '');

BEGIN

    FOR c01_w IN c01 LOOP
        CALL gerar_evento_paciente(c01_w.nr_seq_alerta,
                              NULL,
                              NULL,
                              NULL,
                              wheb_usuario_pck.get_nm_usuario,
                              NULL,
                              NULL,
                              NULL,
                              NULL,
                              NULL,
                              NULL,
                              NULL,
                              NULL,
                              NULL,
                              NULL,
                              'S',
                              NULL,
                              clock_timestamp(),
                              NULL,
                              NULL,
                              NULL,
                              NULL,
                              NULL,
                              NULL,
                              NULL,
                              NULL,
                              'N',
                              NULL,
                              c01_w.nr_seq_evento);
    END LOOP;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
