CREATE OR REPLACE PROCEDURE gerar_alerta_isolamento_pac ( cd_setor_atendimento_p bigint, ds_alerta_p text, ds_lista_p text, nm_usuario_p text) AS $body$
DECLARE


ds_lista_w		varchar(2000);
ds_resultado_w		varchar(2000);
tam_lista_w		bigint;
ie_pos_virgula_w	smallint;
ie_pos_arroba_w		smallint;
ds_listas_w		varchar(2000);
cd_unidade_basica_w	varchar(2000);
cd_unidade_compl_w	varchar(2000);


BEGIN

if (cd_setor_atendimento_p IS NOT NULL AND cd_setor_atendimento_p::text <> '') and (ds_alerta_p IS NOT NULL AND ds_alerta_p::text <> '') and (ds_lista_p IS NOT NULL AND ds_lista_p::text <> '') then
	begin
	ds_lista_w	:= ds_lista_p;

	while	(ds_lista_w IS NOT NULL AND ds_lista_w::text <> '') loop
		begin
		tam_lista_w		:= length(ds_lista_w);
		ie_pos_virgula_w	:= position(',' in ds_lista_w);

		if (ie_pos_virgula_w <> 0) then
			begin
			ds_resultado_w	:= substr(ds_lista_w,1, (ie_pos_virgula_w - 1));
			ds_lista_w	:= substr(ds_lista_w, (ie_pos_virgula_w + 1), tam_lista_w);
			end;
		else
			ds_lista_w	:= null;
		end if;

		if (ds_resultado_w IS NOT NULL AND ds_resultado_w::text <> '') then
			begin
			ie_pos_arroba_w	:= position('@' in ds_resultado_w);

			if (ie_pos_arroba_w <> 0) then
				begin
				cd_unidade_basica_w	:= substr(ds_resultado_w,1, (ie_pos_arroba_w - 2));
				ds_listas_w		:= substr(ds_resultado_w, (ie_pos_arroba_w + 1), tam_lista_w);
				end;
			else
				ds_listas_w	:= null;
			end if;
			end;
		end if;

		if (ds_listas_w IS NOT NULL AND ds_listas_w::text <> '') then
			begin

			ie_pos_arroba_w	:= position('@' in ds_listas_w);

			if (ie_pos_arroba_w <> 0) then
				begin
				cd_unidade_compl_w	:= substr(ds_listas_w,1, (ie_pos_arroba_w - 2));
				ds_listas_w		:= substr(ds_listas_w, (ie_pos_arroba_w + 1), tam_lista_w);
				end;
			else
				ds_listas_w	:= null;
			end if;

			end;
		end if;

		ds_listas_w	:= position('@' in ds_listas_w);

		if ((trim(both cd_unidade_basica_w) IS NOT NULL AND (trim(both cd_unidade_basica_w))::text <> '')) and ((trim(both cd_unidade_compl_w) IS NOT NULL AND (trim(both cd_unidade_compl_w))::text <> '')) and (ds_listas_w = 0) then
			begin
			CALL inserir_alerta_unid_setor(cd_setor_atendimento_p, cd_unidade_basica_w, cd_unidade_compl_w, ds_alerta_p, nm_usuario_p);
			end;
		end if;
		end;
	end loop;
	end;
end if;

commit;

end;
$body$
