CREATE OR REPLACE PROCEDURE gerar_alerta_laudo_lib ( nr_seq_laudo_p bigint, nr_atendimento_p bigint, nm_usuario_p text, ie_commit_p text) AS $body$
DECLARE

				   
nr_seq_evento_w		bigint;
cd_pessoa_fisica_w	varchar(10);
				
c01 CURSOR FOR
	SELECT	a.nr_seq_evento
	from	regra_envio_sms a
	where	a.ie_evento_disp = 'LIBLA'
	and	coalesce(a.ie_situacao,'A') = 'A'
               and a.cd_estabelecimento = WHEB_USUARIO_PCK.GET_CD_ESTABELECIMENTO;

BEGIN
select  substr(coalesce(max(cd_pessoa_fisica),''),1,10)
into STRICT	cd_pessoa_fisica_w
from	atendimento_paciente
where	nr_atendimento = nr_atendimento_p;

open C01;
loop
fetch C01 into	
	nr_seq_evento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin	
	CALL gerar_evento_paciente(	nr_seq_evento_w,
				nr_atendimento_p, 
				cd_pessoa_fisica_w, 
				nr_seq_laudo_p,
				nm_usuario_p,
				null,
				null,
        			null,
				null,
				null,
				null,
				null,
				null,
				null,
				null,
				ie_commit_p);
				
	end;
end loop;
close C01;

if (ie_commit_p = 'S') then
	commit;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
