CREATE OR REPLACE PROCEDURE gerar_alerta_proj_migr_os ( nr_seq_os_projeto_p bigint, nr_seq_os_p bigint, cd_funcao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_projeto_w	bigint;
cd_pessoa_fisica_w	varchar(10);
nr_seq_alerta_w		bigint;

c01 CURSOR FOR
SELECT	a.nr_sequencia
from	proj_ordem_servico b,
	proj_projeto a
where	b.nr_seq_proj = a.nr_sequencia
and	a.nr_seq_classif = 14
and	a.cd_funcao = cd_funcao_p
and	a.nr_seq_ordem_serv = nr_seq_os_projeto_p
and	b.nr_seq_ordem = nr_seq_os_p
order by
	a.nr_sequencia;

c02 CURSOR FOR
SELECT	b.cd_pessoa_fisica
from	proj_equipe_papel b,
	proj_equipe a
where	b.nr_seq_equipe = a.nr_sequencia
and	a.nr_seq_proj = nr_seq_projeto_w
and	(b.cd_pessoa_fisica IS NOT NULL AND b.cd_pessoa_fisica::text <> '')
and	coalesce(b.ie_alerta,'S') = 'S';


BEGIN
if (nr_seq_os_projeto_p IS NOT NULL AND nr_seq_os_projeto_p::text <> '') and (nr_seq_os_p IS NOT NULL AND nr_seq_os_p::text <> '') and (cd_funcao_p IS NOT NULL AND cd_funcao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	open c01;
	loop
	fetch c01 into nr_seq_projeto_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		open c02;
		loop
		fetch c02 into cd_pessoa_fisica_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
			begin
			select	nextval('proj_alerta_seq')
			into STRICT	nr_seq_alerta_w
			;

			insert into proj_alerta(
				nr_sequencia,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				dt_atualizacao,
				nm_usuario,
				nr_seq_projeto,
				ie_alerta,
				dt_alerta,
				nm_usuario_alerta,
				cd_pessoa_destino_alerta,
				dt_ciente)
			values (
				nr_seq_alerta_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_projeto_w,
				'OS',
				clock_timestamp(),
				nm_usuario_p,
				cd_pessoa_fisica_w,
				null);
			end;
		end loop;
		close c02;
		end;
	end loop;
	close c01;
	end;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
