CREATE OR REPLACE PROCEDURE gerar_alerta_usuario_regra ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


retorno_w			integer;
C02				integer;
ds_alerta_w			varchar(2000);
ds_sql_w			varchar(2000);
cd_perfil_w			integer;
ie_sensitive_w	varchar(1);

c01 CURSOR FOR
	SELECT  b.ds_sql,
			b.ie_sensitive
	from	regra_alerta a,
		regra_alerta_comando b
	where	a.nr_sequencia 			= b.nr_seq_regra
	and	coalesce(a.ie_situacao,'A') 		= 'A'
	and	coalesce(a.cd_perfil,cd_perfil_w) 	= cd_perfil_w
	order by coalesce(a.cd_perfil,0);


BEGIN

cd_perfil_w	:= wheb_usuario_pck.get_cd_perfil;

delete	from alerta_geral_usuario;
commit;

open C01;
loop
fetch C01 into
	ds_sql_w,
	ie_sensitive_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	C02 := DBMS_SQL.OPEN_CURSOR;
	DBMS_SQL.PARSE(C02, ds_sql_w, dbms_sql.Native);
	DBMS_SQL.DEFINE_COLUMN(C02,1,ds_alerta_w,2000);

	if (position(':NR_ATENDIMENTO' in upper(ds_sql_w)) > 0) then
		DBMS_SQL.BIND_VARIABLE(C02,'NR_ATENDIMENTO', nr_atendimento_p);
	end if;
	if (position(':CD_PESSOA_FISICA' in upper(ds_sql_w)) > 0) then
		DBMS_SQL.BIND_VARIABLE(C02,'CD_PESSOA_FISICA', cd_pessoa_fisica_p);
	end if;
	retorno_w := DBMS_SQL.execute(C02);
	while( DBMS_SQL.FETCH_ROWS(C02) > 0 ) loop
		begin
		DBMS_SQL.COLUMN_VALUE(C02,1,ds_alerta_w);
		insert	into	alerta_geral_usuario(	nr_sequencia,
					ds_alerta,
					nm_usuario,
					nm_usuario_nrec,
					dt_atualizacao,
					dt_atualizacao_nrec,
					ie_sensitive)
		values (	nextval('alerta_geral_usuario_seq'),
					ds_alerta_w,
					nm_usuario_p,
					nm_usuario_p,
					clock_timestamp(),
					clock_timestamp(),
					ie_sensitive_w);
		commit;
		end;
	end loop;
	DBMS_SQL.CLOSE_CURSOR(C02);
	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
