SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_alerta_vencimento_conta (nr_atendimento_p text, dt_alta_p timestamp, nm_usuario_p text) AS $body$
DECLARE


cd_convenio_w		bigint;
cd_estabelecimento_w	bigint;
nr_dias_venc_atend_w	bigint;
nr_dias_alerta_w	bigint;
cd_pessoa_fisica_w	varchar(10);
ds_alerta_w		varchar(255);
dt_alerta_w		timestamp;
dt_fim_alerta_w		timestamp;


BEGIN

cd_convenio_w :=  obter_convenio_atendimento(nr_atendimento_p);

select	max(cd_estabelecimento),
	max(cd_pessoa_fisica)
into STRICT	cd_estabelecimento_w,
	cd_pessoa_fisica_w
from 	atendimento_paciente
where	nr_atendimento = nr_atendimento_p;

select	max(nr_dias_venc_atend),
        max(nr_dias_alerta)
into STRICT	nr_dias_venc_atend_w,
	nr_dias_alerta_w
from	convenio_estabelecimento
where	cd_convenio = cd_convenio_w
and	cd_estabelecimento = cd_estabelecimento_w;

if (coalesce(nr_dias_venc_atend_w,0) > 0) and (coalesce(nr_dias_alerta_w,0) > 0) then

	dt_alerta_w	:= dt_alta_p + nr_dias_alerta_w;
	dt_fim_alerta_w	:= dt_alerta_w + nr_dias_venc_atend_w;

	ds_alerta_w := WHEB_MENSAGEM_PCK.get_texto(457725,'dt_fim_alerta='||PKG_DATE_FORMATERS.TO_VARCHAR(dt_fim_alerta_w, 'shortDate', WHEB_USUARIO_PCK.GET_CD_ESTABELECIMENTO, nm_usuario_p));

	insert into atendimento_alerta(	cd_estabelecimento,
					nr_atendimento,
					ds_alerta,
					dt_atualizacao,
					dt_alerta,
					dt_liberacao,
					dt_fim_alerta,
					ie_situacao,
					nm_usuario,
					nr_sequencia)
			values (	cd_estabelecimento_w,
					nr_atendimento_p,
					ds_alerta_w,
					clock_timestamp(),
					dt_alerta_w,
					dt_alerta_w,
					dt_fim_alerta_w,
					'A',
					nm_usuario_p,
					nextval('atendimento_alerta_seq'));
end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
