CREATE OR REPLACE PROCEDURE gerar_alta_tesouraria (nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
dt_alta_tesouraria_w	timestamp;
dt_alta_medica_w		timestamp;
vl_parametro_w			varchar(2);				
				 

BEGIN 
 
select	dt_alta_tesouraria, 
		dt_alta_medico 
into STRICT	dt_alta_tesouraria_w, 
		dt_alta_medica_w 
from	atendimento_paciente 
where	nr_atendimento = nr_atendimento_p;
 
vl_parametro_w := obter_param_usuario(3111, 312, obter_perfil_ativo, nm_usuario_p, obter_estabelecimento_ativo, vl_parametro_w);
 
if (coalesce(vl_parametro_w,'N') = 'N') and (coalesce(dt_alta_medica_w::text, '') = '') then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(325997);
end if;
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (coalesce(dt_alta_tesouraria_w::text, '') = '') then 
	update	atendimento_paciente 
	set	dt_alta_tesouraria 	= clock_timestamp(), 
		nm_usuario		= nm_usuario_p 
	where	nr_atendimento		= nr_atendimento_p;
	 
	CALL Gravar_Hist_Alta_Saida_Real(nr_atendimento_p, obter_desc_expressao(10652295), clock_timestamp(), nm_usuario_p);
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
