CREATE OR REPLACE PROCEDURE gerar_alteracao_adep ( nr_prescricao_p bigint, nr_seq_justificativa_p bigint, nm_usuario_p text, ds_observacao_p text) AS $body$
BEGIN

update	prescr_medica
set	nr_seq_justificativa 	= nr_seq_justificativa_p,
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p,
	ds_justificativa 	= substr(CASE WHEN ds_justificativa = NULL THEN null  ELSE ds_justificativa || chr(13) END  ||ds_observacao_p,1,2000)
where	nr_prescricao 		= nr_prescricao_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
