BEGIN
if (cd_evolucao_p IS NOT NULL AND cd_evolucao_p::text <> '') and (nr_seq_classif_p IS NOT NULL AND nr_seq_classif_p::text <> '') then
	
	select 	coalesce(max('S'),'N')
