		set      nr_seq_classif_medico  = nr_seq_classif_p
		where    nr_seq_evo_paciente      = cd_evolucao_p;
	else
		insert into evolucao_paciente_compl(
		nr_seq_evo_paciente,
		nr_seq_classif_medico)
		values (
		cd_evolucao_p,
		nr_seq_classif_p);
	end if;
	
	commit;
	
end if;

end;
