CREATE OR REPLACE PROCEDURE gerar_alterar_dia_real_onc ( nr_seq_atendimento_p bigint, nm_usuario_p text, ie_commit_p text default 'S') AS $body$
DECLARE


nr_ciclo_w			bigint;
nr_seq_paciente_w		bigint;
prim_dt_prevista_ciclo_w	timestamp;
dt_prevista_w			timestamp;
ds_dia_w			varchar(10);
qt_dias_w			bigint;
nr_seq_atendimento_w		bigint;

c01 CURSOR FOR
	SELECT	trunc(dt_prevista),
		nr_seq_atendimento
	from	paciente_atendimento
	where	nr_seq_paciente = nr_seq_paciente_w
	and	nr_ciclo = nr_ciclo_w;

c01_w	c01%rowtype;


BEGIN

if (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') then
	select	nr_ciclo,
		nr_seq_paciente
	into STRICT	nr_ciclo_w,
		nr_seq_paciente_w
	from	paciente_atendimento
	where	nr_seq_atendimento = nr_seq_atendimento_p;

	select	min(trunc(dt_prevista))
	into STRICT	prim_dt_prevista_ciclo_w
	from	paciente_atendimento
	where	nr_seq_paciente = nr_seq_paciente_w
	and	nr_ciclo = nr_ciclo_w;

	open C01;
	loop
	fetch C01 into
		dt_prevista_w,
		nr_seq_atendimento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (dt_prevista_w = prim_dt_prevista_ciclo_w) then
			ds_dia_w	:= 'D1';
		else
			ds_dia_w	:= 'D' || to_char(trunc( dt_prevista_w -  prim_dt_prevista_ciclo_w )+1);
		end if;

		update	paciente_atendimento
		set	ds_dia_ciclo_real = ds_dia_w
		where	nr_seq_atendimento = nr_seq_atendimento_w;
		end;
	end loop;
	close C01;

	if (coalesce(ie_commit_p,'S') = 'S') then
		commit;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
