SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_alt_status_regulacao ( nr_seq_parecer_int_w bigint, ie_status_p text) AS $body$
DECLARE


	nr_seq_regulacao_w	regulacao_atend.nr_sequencia%type;


BEGIN

	if (nr_seq_parecer_int_w IS NOT NULL AND nr_seq_parecer_int_w::text <> '' AND ie_status_p IS NOT NULL AND ie_status_p::text <> '') then

		select	max(nr_sequencia)
		into STRICT	nr_seq_regulacao_w
		from	regulacao_atend
		where	nr_seq_resp_parecer_ori = nr_seq_parecer_int_w;

		if (nr_seq_regulacao_w IS NOT NULL AND nr_seq_regulacao_w::text <> '') then

			update	PARECER_MEDICO
			set		dt_liberacao = clock_timestamp(),
					nm_usuario = wheb_usuario_pck.get_nm_usuario,
					dt_atualizacao = clock_timestamp()
			where	NR_SEQ_INTERNO = nr_seq_parecer_int_w;

			update	WL_WORKLIST
			set		dt_final_real = clock_timestamp(),
					nm_usuario = wheb_usuario_pck.get_nm_usuario,
					dt_atualizacao = clock_timestamp()
			where	nr_seq_regulacao_atend = nr_seq_regulacao_w;

			CALL Alterar_status_regulacao(nr_seq_regulacao_w, ie_status_p, null);

			commit;

		end if;

	end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
