CREATE OR REPLACE PROCEDURE gerar_anexo_atend_laudo ( nr_atendimento_p bigint, nr_sequencia_p bigint, nm_usuario_p text, ie_opcao_p text) AS $body$
DECLARE

 
ds_arquivo_w		varchar(255);
nr_seq_imagem_w		bigint;
ie_atendimento_ext_w 	bigint;
nr_sequencia_w		bigint;

/* 
OPÇÕES - Parametro 247 - Laudo Paciente 
'E' - Atendimento Externo 
'S' - Todos os tipos de atendimentos 
*/
 
 
C01 CURSOR FOR 
	SELECT	ds_arquivo 
	from	atendimento_paciente_anexo 
	where	nr_atendimento = nr_atendimento_p;


BEGIN 
 
select 	max(ie_tipo_atendimento) 
into STRICT	ie_atendimento_ext_w 
from	atendimento_paciente 
where	nr_atendimento = nr_atendimento_p;
 
if 	((ie_opcao_p = 'E' AND ie_atendimento_ext_w = 7) or (ie_opcao_p = 'S')) then 
	 
	open C01;
	loop 
	fetch C01 into 
		ds_arquivo_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
		 
		select (coalesce(max(nr_seq_imagem),0) + 1) 
		into STRICT	nr_seq_imagem_w 
		from	laudo_paciente_imagem 
		where	nr_sequencia = nr_sequencia_p;
		 
		insert into laudo_paciente_imagem( 
			nr_sequencia, 
			nr_seq_imagem, 
			ds_arquivo_imagem, 
			dt_atualizacao, 
			nm_usuario) 
		values ( 
			nr_sequencia_p, 
			nr_seq_imagem_w, 
			ds_arquivo_w, 
			clock_timestamp(), 
			nm_usuario_p);
 
	end;
	end loop;
	close C01;	
 
	commit;
 
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
