CREATE OR REPLACE PROCEDURE gerar_aop_ano (dt_referencia_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_ordem_w		bigint;
nr_seq_grupo_des_w	bigint;
dt_ordem_servico_W	timestamp;	
NR_SEQ_GERENCIA_w	bigint;
	
C01 CURSOR FOR 
	SELECT	a.nr_sequencia, 
		nr_seq_grupo_des, 
		trunc(dt_ordem_servico,'month'), 
		nr_seq_gerencia 
	from	man_ordem_servico_v a, 
		GRUPO_DESENVOLVIMENTO b 
	where	trunc(dt_ordem_servico,'year') = trunc(dt_referencia_p,'year') 
	and	nr_seq_grupo_des = b.nr_sequencia 
	and exists (	SELECT	1 
			from 	man_estagio_processo c, 
				man_ordem_serv_estagio b 
			where	b.nr_seq_estagio		= c.nr_sequencia 
			and	c.ie_desenv		= 'S' 
			and	a.nr_sequencia		= b.nr_seq_ordem) 
	order by 1;
			

BEGIN 
 
delete	FROM man_aop_desenv 
where	trunc(dt_ordem_servico,'year') = trunc(dt_referencia_p,'year');
 
open C01;
loop 
fetch C01 into	 
	nr_seq_ordem_w, 
	nr_seq_grupo_des_w, 
	dt_ordem_servico_w, 
	nr_seq_gerencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	 
	insert into man_aop_desenv( 
		nr_sequencia, 
		dt_atualizacao, 
		nm_usuario, 
		dt_atualizacao_nrec, 
		nm_usuario_nrec, 
		nr_seq_ordem, 
		nr_seq_grupo_des, 
		dt_ordem_servico, 
		nr_seq_gerencia, 
		IE_TIPO) 
	 values ( 
		nextval('man_aop_desenv_seq'), -- >>> ATRIBUTO OBRIGATÓRIO !!! <<< 
		clock_timestamp(), -- >>> ATRIBUTO OBRIGATÓRIO !!! <<< 
		nm_usuario_p, -- >>> ATRIBUTO OBRIGATÓRIO !!! <<< 
		clock_timestamp(), 
		nm_usuario_p, 
		nr_seq_ordem_w, 
		nr_seq_grupo_des_w, 
		dt_ordem_servico_w, 
		nr_seq_gerencia_w, 
		'D'); -- >>> ATRIBUTO OBRIGATÓRIO !!! <<< 
	
	 
	 
	 
	end;
end loop;
close C01;
 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
