SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_arquivo_dirf_mensal ( nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_registros_w		bigint;


BEGIN

select 	count(*)
into STRICT	qt_registros_w
from	dirf_lote_mensal d,
	dirf_agrupar_lote a,
	dirf_lote l
where	d.nr_sequencia = a.nr_seq_lote_mensal
and	l.nr_sequencia = a.nr_seq_lote_anual;

if (qt_registros_w = 0) then
	/*'Não foi possível gerar o arquivo, pois não foram selecionados lotes mensais para compor o lote anual.*/

	CALL wheb_mensagem_pck.Exibir_Mensagem_Abort(240757);
end if;

delete
from	w_dirf_arquivo
where	nm_usuario = nm_usuario_p;
commit;

CALL dirf_registro_dirf(nr_seq_lote_p,nm_usuario_p);
CALL dirf_registro_respo(nr_seq_lote_p,nm_usuario_p);
CALL dirf_registro_decpj(nr_seq_lote_p,nm_usuario_p);
CALL dirf_mensal_registro_idrec(nr_seq_lote_p,nm_usuario_p);
CALL dirf_mensal_registro_vpeim(nr_seq_lote_p,nm_usuario_p);
CALL dirf_registro_scp(nr_seq_lote_p, nm_usuario_p);
CALL dirf_registro_fimdirf(nm_usuario_p);

update 	dirf_lote
set	dt_envio = clock_timestamp()
where	nr_sequencia = nr_seq_lote_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_arquivo_dirf_mensal ( nr_seq_lote_p bigint, nm_usuario_p text) FROM PUBLIC;

