SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_assinatura_pendente ( nm_tabela_p text, nr_sequencia_p bigint, nr_seq_assinatura_p bigint) AS $body$
DECLARE


nr_sequencia_w				bigint;
nr_seq_assinatura_reg_w		bigint;
qt_reg_assinado_w			bigint;
dt_assinatura_w				timestamp;
dt_assinatura_reg_w			timestamp;

C01 CURSOR FOR
	SELECT	nr_seq_assinatura
	from	atend_escala_braden_q
	where	nr_seq_prescr = nr_sequencia_p
	order by nr_sequencia;



BEGIN

Select 	obter_data_assinatura_digital(nr_seq_assinatura_p)
into STRICT	dt_assinatura_w
;

if (nr_seq_assinatura_p > 0) and (nr_sequencia_p > 0) and (dt_assinatura_w IS NOT NULL AND dt_assinatura_w::text <> '')then

	if (upper(nm_tabela_p) = 'PE_PRESCRICAO' ) then

		open C01;
		loop
		fetch C01 into
			nr_seq_assinatura_reg_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin

			if (coalesce(nr_seq_assinatura_reg_w,0) > 0)then

				Select 	obter_data_assinatura_digital(nr_seq_assinatura_reg_w)
				into STRICT	dt_assinatura_reg_w
				;

				if (coalesce(dt_assinatura_reg_w::text, '') = '') then

					update 	tasy_assinatura_digital
					set 	dt_assinatura = dt_assinatura_w
					where 	nr_sequencia = nr_seq_assinatura_reg_w;

					commit;

				end if;

			end if;

			end;
		end loop;
		close C01;

	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
