SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_atendimento_precaucao ( nr_seq_precaucao_p bigint, nr_atendimento_p bigint, ie_opcao_p text, nr_seq_motivo_isol_p bigint, nm_usuario_p text, nr_seq_atend_precaucao_p bigint default null, nr_seq_motivos_isol_p text default null, ds_observacao_p text default null, ie_termino_p text default null, cd_responsavel_p text default null, dt_referencia_p timestamp default null) AS $body$
DECLARE


nr_seq_motivo_isol_w	bigint := '';
nr_sequencia_ww		bigint;
ds_lista_w		varchar(1000);
tam_lista_w		bigint;
ie_pos_virgula_w	smallint;
nr_seq_precaucao_w	bigint;


BEGIN

/*
ie_opcao_p :
I = Início
T= Término
*/
/*
ie_termino_p:
U - Um registro
T - Todos os registros
*/
if (ie_opcao_p = 'I') then  --Início
	if (nr_seq_motivo_isol_p > 0) then
		nr_seq_motivo_isol_w := nr_seq_motivo_isol_p;
	end if;


	select	nextval('atendimento_precaucao_seq')
	into STRICT	nr_seq_precaucao_w
	;

	insert into atendimento_precaucao(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_precaucao,
		dt_inicio,
		dt_termino,
		nr_atendimento,
		dt_registro,
		nr_seq_motivo_isol,
		dt_liberacao,
		ds_observacao,
		cd_responsavel,
		ie_situacao)
	values (
		nr_seq_precaucao_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_precaucao_p,
		clock_timestamp(),
		null,
		nr_atendimento_p,
		clock_timestamp(),
		nr_seq_motivo_isol_w,
		clock_timestamp(),
		ds_observacao_p,
		cd_responsavel_p,
		'A');


	if (nr_seq_motivos_isol_p IS NOT NULL AND nr_seq_motivos_isol_p::text <> '') then

		ds_lista_w := nr_seq_motivos_isol_p;

		if (substr(ds_lista_w,length(ds_lista_w) - 1, length(ds_lista_w))	<> ',') then
			ds_lista_w	:= ds_lista_w ||',';
		end if;


		while(ds_lista_w IS NOT NULL AND ds_lista_w::text <> '') loop
			begin


			tam_lista_w		:= length(ds_lista_w);
			ie_pos_virgula_w	:= position(',' in ds_lista_w);

			if (ie_pos_virgula_w <> 0) then
				nr_sequencia_ww		:= (substr(ds_lista_w,1,(ie_pos_virgula_w - 1)))::numeric;
				ds_lista_w		:= substr(ds_lista_w,(ie_pos_virgula_w + 1),tam_lista_w);
			end if;
			if (coalesce(nr_sequencia_ww,0) <> 0) then

				insert into atend_precaucao_motivo(
					nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nr_seq_atend_precaucao,
					nr_seq_motivo_isol)
				values (
					nextval('atend_precaucao_motivo_seq'),
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					nr_seq_precaucao_w,
					nr_sequencia_ww);
			end if;

			end;

		end loop;

	end if;



elsif (ie_opcao_p = 'T') then --Término
	CALL finalizar_atend_precaucao(nr_atendimento_p, dt_referencia_p, nr_seq_atend_precaucao_p, ie_termino_p, nm_usuario_p);

end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
