CREATE OR REPLACE PROCEDURE gerar_atend_escuta ( nr_atendimento_p bigint, cd_profissional_p text, nm_usuario_p text) AS $body$
DECLARE

 
qt_registro_w	integer;
cd_pessoa_fisica_w	varchar(10);


BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
	begin 
	select	count(*) 
	into STRICT	qt_registro_w 
	from	atend_escuta_inicial 
	where	nr_atendimento = nr_atendimento_p;
 
	if (coalesce(qt_registro_w,0) = 0) then 
		begin 
		select	cd_pessoa_fisica 
		into STRICT	cd_pessoa_fisica_w 
		from	atendimento_paciente 
		where	nr_atendimento = nr_atendimento_p;
 
		insert into atend_escuta_inicial 
		values (	nextval('atend_escuta_inicial_seq'), 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			cd_pessoa_fisica_w, 
			nr_atendimento_p, 
			'A', 
			null, 
			null, 
			null, 
			null, 
			clock_timestamp(), 
			cd_profissional_p);
		end;
	end if;
	end;
end if;
 
commit;
 
end;
