SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_atend_pac_inf_evol (nr_atendimento_p bigint, cd_evolucao_p bigint) AS $body$
DECLARE


qt_reg_w 			bigint := 0;


BEGIN

select	count(*)
into STRICT	qt_reg_w
from	atendimento_paciente_inf
where	nr_atendimento = nr_atendimento_p;

if (qt_reg_w	=  0)	then
	insert into atendimento_paciente_inf(nr_sequencia,
									nr_atendimento,
									dt_atualizacao,
									nm_usuario,
									dt_atualizacao_nrec,
									nm_usuario_nrec,
                                    cd_evolucao
									)
							values (nextval('atendimento_paciente_inf_seq'),
									nr_atendimento_p,
									clock_timestamp(),
									wheb_usuario_pck.get_nm_usuario,
									clock_timestamp(),
									wheb_usuario_pck.get_nm_usuario,
									cd_evolucao_p);
else
	update	atendimento_paciente_inf
	set		cd_evolucao	= cd_evolucao_p
	where	nr_atendimento = nr_atendimento_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
