CREATE OR REPLACE PROCEDURE gerar_atend_presc_anestesica ( nr_atendimento_p bigint, nr_prescricao_p bigint) AS $body$
DECLARE

 
					 
ie_gerar_lote_w		varchar(15) := 'N';
cd_setor_atendimento_w	integer;	
nm_usuario_p		varchar(15);			
					 

BEGIN 
 
nm_usuario_p := wheb_usuario_pck.get_nm_usuario;
 
ie_gerar_lote_w := obter_param_usuario(874, 13, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_gerar_lote_w);
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then 
	 
	update	prescr_medica 
	set	nr_atendimento	= nr_atendimento_p 
	where	nr_prescricao	= nr_prescricao_p 
	and	coalesce(nr_atendimento::text, '') = '';
	 
	commit;
	 
	if (ie_gerar_lote_w = 'S') then 
		cd_setor_atendimento_w := obter_setor_atendimento(nr_atendimento_p);
		 
		update	prescr_medica 
		set	cd_setor_atendimento	= cd_setor_atendimento_w 
		where	nr_prescricao 		= nr_prescricao_p;	
		 
		update	ap_lote 
		set	cd_setor_atendimento	= cd_setor_atendimento_w 
		where	ie_status_lote 		= 'G' 
		and	nr_prescricao		= nr_prescricao_p;	
		 
		CALL Gerar_Lote_Atend_Prescricao(nr_prescricao_p, 0, 0, 'N', nm_usuario_p, 'VAA');
		commit;
	end if;
	 
 
end if;	
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
