CREATE OR REPLACE PROCEDURE gerar_atend_previsao_alta_diag ( nr_atendimento_p bigint, cd_profissional_p text, dt_prevista_alta_p timestamp, ie_prob_alta_p text, nr_dias_prev_p bigint, nm_usuario_p text, nr_seq_anterior_p bigint default null, ie_commit_p text default 'S') AS $body$
DECLARE

				
nr_sequencia_w	atend_previsao_alta.nr_sequencia%type;


BEGIN
if (coalesce(nr_atendimento_p,0) > 0) and (cd_profissional_p IS NOT NULL AND cd_profissional_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (nr_dias_prev_p IS NOT NULL AND nr_dias_prev_p::text <> '') and (dt_prevista_alta_p IS NOT NULL AND dt_prevista_alta_p::text <> '') and (dt_prevista_alta_p > clock_timestamp())	then
	insert into atend_previsao_alta(
		nr_sequencia,
		cd_profissional,
		dt_atualizacao,
		dt_atualizacao_nrec,
		dt_previsto_alta,
		dt_registro,
		dt_liberacao,
		ie_alta_hospitalar,
		ie_probabilidade_alta,
		ie_situacao,
		nm_usuario,
		nm_usuario_nrec,
		nr_atendimento,
		nr_dias_prev_alta)
	values (
		nextval('atend_previsao_alta_seq'),
		cd_profissional_p,
		clock_timestamp(),
		clock_timestamp(),
		dt_prevista_alta_p,
		clock_timestamp(),
		clock_timestamp(),
		'S',
		ie_prob_alta_p,
		'A',
		nm_usuario_p,
		nm_usuario_p,
		nr_atendimento_p,
		nr_dias_prev_p);
		
	
	
	if (nr_seq_anterior_p IS NOT NULL AND nr_seq_anterior_p::text <> '') then
		update	atend_previsao_alta
		set	dt_inativacao = clock_timestamp(),
			nm_usuario_inativacao = nm_usuario_p
		where	nr_sequencia = nr_seq_anterior_p;
	end if;
	
	if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then
		commit;
	end if;
	
	select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	atend_previsao_alta
	where	nr_atendimento = nr_atendimento_p
	and		dt_previsto_alta = dt_prevista_alta_p
	and		cd_profissional = cd_profissional_p
	and		ie_probabilidade_alta = ie_prob_alta_p
	and		nr_dias_prev_alta = nr_dias_prev_p;
	
	CALL wl_gerar_finalizar_tarefa('ED','F', nr_atendimento_p,0,nm_usuario_p,clock_timestamp(),'S',null,null,null,null,null,null,null,null,null,null,null,null,null,null,nr_sequencia_w);
end if;			
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
