CREATE OR REPLACE PROCEDURE gerar_atend_previsao_alta ( nr_atendimento_p bigint, cd_profissional_p text, dt_prevista_alta_p timestamp, ie_prob_alta_p text, nm_usuario_p text, ds_justificativa_p text default null, ie_acao_p text default 'I') AS $body$
BEGIN
if (coalesce(nr_atendimento_p,0) > 0) and (cd_profissional_p IS NOT NULL AND cd_profissional_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (dt_prevista_alta_p IS NOT NULL AND dt_prevista_alta_p::text <> '') and (dt_prevista_alta_p > clock_timestamp())	then
	
	if (ie_acao_p in ('I','A','E')) then
	
		update	atend_previsao_alta
		set	dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p,
			ie_situacao = 'I',
			dt_inativacao = clock_timestamp()
		where	nr_atendimento = nr_atendimento_p
		and	ie_situacao = 'A';
		
	end if;
		
	if (ie_acao_p in ('I','A')) then

		insert into atend_previsao_alta(
			nr_sequencia,
			cd_profissional,
			dt_atualizacao,
			dt_atualizacao_nrec,
			dt_previsto_alta,
			dt_registro,
			dt_liberacao,
			ie_alta_hospitalar,
			ie_probabilidade_alta,
			ie_situacao,
			nm_usuario,
			nm_usuario_nrec,
			nr_atendimento,
			ds_justificativa)
		values (
			nextval('atend_previsao_alta_seq'),
			cd_profissional_p,
			clock_timestamp(),
			clock_timestamp(),
			dt_prevista_alta_p,
			clock_timestamp(),
			clock_timestamp(),
			'S',
			ie_prob_alta_p,
			'A',
			nm_usuario_p,
			nm_usuario_p,
			nr_atendimento_p,
			ds_justificativa_p);

	end if;

	commit;
end if;			
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
